class YunYVideoInfo(VideoInfo):
    def get_info(self):
        data = paras_nuxt_data(self.html)
        link = ""
        next_link = ""
        un_id = 0
        lines = []
        for k, vd in data.get("ShallowReactive").get("data").items():
            for kk, vv in vd.items():
                if kk == "playSelectItem":
                    link = vv.get("playUrl")
                    video_id = vv.get("id")
                if kk == "playDetail":
                    lines = vv.get("playDetail").get("lines")
        i = 0
        flag = 0
        for line in lines:
            for i in line.get("selects"):
                if flag == 1:
                    next_link = i
                if i.get("id") == un_id:
                    flag = 1

    @staticmethod
    def get_ts_url(link, string):
        """
        :param link: m3u8地址
        :param string: ts名
        :return:
        """
        pre_url = "/".join(link.split("/")[:-1])
        return url_join(pre_url, string)

    def get_m3u8_url(self):
        url = super().get_m3u8_url()
        m3u8_index = req.get(url)  # m3u8文件
        index = ""
        for i in m3u8_index.text.split("\n"):
            if not i.startswith("#") and (i.endswith("index.m3u8") or i.endswith("mixed.m3u8")):
                index = i
        m3u8_url = url_join("/".join(url.split("/")[:-1]), index)
        return m3u8_url


